css:
section,
.section {
  position: relative;
  margin-top: 0;
}
section:last-child,
.section:last-child {
  margin-bottom: 0;
}
section.bordered,
.section.bordered {
  margin: 0;
  padding: 20px 0;
  border-top: 1px solid rgba(0, 0, 0, 0.1);
  border-bottom: 1px solid #9f9f9f;
}
section .section-heading:first-child,
.section .section-heading:first-child {
  margin-top: 0;
  font-weight: bold;
  color: #000000;
}
div > section:first-child,
form > section:first-child,
div > .section:first-child,
form > .section:first-child {
  border-top: none;
  padding-top: 0;
}
div > section:last-child,
form > section:last-child,
div > .section:last-child,
form > .section:last-child {
  border-bottom: none;
  padding-bottom: 0;
}
