/* ONE light UI variables
** ---------------------------------------------- */
/* Official variables
** These variables must be defined in every theme
** Source: https://github.com/atom/atom/blob/master/static/variables/ui-variables.less
** ---------------------------------------------- */
/* Custom variables
** These variables are only used in this theme
** ---------------------------------------------- */
html {
  font-size: 11px;
}
/* Packages variables
** These variables are used to override packages
** ---------------------------------------------- */
/* Debug
** Output variables to the top of the UI
** ------------------------------------- */
.styleguide {
  position: relative;
  overflow: auto;
  font-size: 12px;
}
.styleguide > section.bordered {
  padding: 3em;
}
.styleguide > section :last-child {
  margin-bottom: 0;
}
.styleguide > section.collapsed > .section-heading {
  display: block;
  margin: 0;
  color: #575757;
}
.styleguide > section.collapsed > * {
  display: none;
}
.styleguide section .section-heading {
  cursor: pointer;
  font-weight: bold;
  font-size: 1.6em;
  color: #5c3d3d;
}
.styleguide section .section-heading:hover {
  color: #5c3d3d;
}
.styleguide section[data-name="styleguide"] {
  border-top: none;
}
.styleguide .popover-list {
  position: relative;
}
.styleguide .select-list atom-text-editor[mini],
.styleguide .popover-list atom-text-editor[mini] {
  height: 27px;
}
.styleguide .tooltip {
  position: relative;
  opacity: 1;
  display: inline-block;
  margin-right: 1.5em;
}
.styleguide .btn-collapse-all {
  position: absolute;
  right: 1.5em;
  top: 1.5em;
  z-index: 100;
}
.example {
  display: -webkit-flex;
  border: 1px solid #e0e0e0;
  border-radius: 3px;
  margin: 1.5em 0;
}
.example > * {
  width: 50%;
}
.example .example-rendered {
  position: relative;
  padding: 1.5em;
  background: #f5f5f5;
  overflow: hidden;
  border-radius: 3px 0 0 3px;
}
.example .example-rendered .ui-site-1,
.example .example-rendered .ui-site-2,
.example .example-rendered .ui-site-3,
.example .example-rendered .ui-site-4,
.example .example-rendered .ui-site-5 {
  height: 10px;
  width: 100px;
}
.example .example-code {
  position: relative;
  background-color: #ffffff;
  border-left: 1px solid #e0e0e0;
}
.example .example-code.show-example-space-pen .example-space-pen {
  display: block;
}
.example .example-code.show-example-html .example-html {
  display: block;
}
.example .example-code .btn-group {
  position: absolute;
  top: 1.5em;
  right: 1.5em;
  z-index: 10;
}
