css:
.btn {
  color: #aaaaaa;
  border-radius: 2px;
  border: none;
  text-shadow: none;
  height: 27px;
  line-height: 25px;
  padding: 0 10px;
  font-size: 11px;
  z-index: 0;
  background-color: #43484d;
}
.btn:hover {
  background-color: #4f555b;
}
.btn.selected,
.btn.selected:hover {
  z-index: 1;
  background-color: #5c6064;
}
.btn.btn-xs,
.btn-group-xs > .btn {
  padding: 2.5px 5px;
  font-size: 9px;
  height: auto;
  line-height: 1.3em;
}
.btn.btn-sm,
.btn-group-sm > .btn {
  padding: 2.5px 5px;
  height: auto;
  line-height: 1.3em;
}
.btn.btn-lg,
.btn-group-lg > .btn {
  font-size: 13px;
  padding: 8px 12px;
  height: auto;
  line-height: 1.3em;
}
.btn-group > .btn {
  border-left: 1px solid rgba(0, 0, 0, 0.5);
  border-right: 1px solid rgba(0, 0, 0, 0.5);
}
.btn-group > .btn:first-child {
  border-left: none;
  border-top-left-radius: 2px;
  border-bottom-left-radius: 2px;
}
.btn-group > .btn:last-child,
.btn-group > .btn.selected:last-child,
.btn-group > .dropdown-toggle {
  border-right: none;
  border-top-right-radius: 2px;
  border-bottom-right-radius: 2px;
}
.btn-toolbar > .btn-group + .btn-group,
.btn-toolbar > .btn-group + .btn,
.btn-toolbar > .btn + .btn {
  float: none;
  display: inline-block;
  margin-left: 0;
}
.btn-toolbar > * {
  margin-right: 5px;
}
.btn-toolbar > *:last-child {
  margin-right: 0;
}
